import React, { useState } from 'react';
import { useEffect } from 'react';
import { getRecommendations } from '../api';


const Reccomendations = ({
  token,
  recommendations,
  setReccomendations,
  seeds,
  seed_type
}) => {
  const [display_recs, setDisplayingRecs] = useState(false);

  useEffect(()=>{
    if(!seeds.length) return;
    getRecommendations(token, seeds, seed_type)
    .then(response => response.json())
    .then(response => {
      setRecomendations(response.tracks);
    });
  });

  return(
  <>
    {seeds.length
     ? <button >
      Show Recommendations
     </button>
    : null
    }
    {recommendations.length && display_recs
    ? <div className="rec-container">
        <h3>Reccomendations: </h3>
        {recommendations.map(reccomendation => 
          <div className='rec-item'>
            {reccomendation.name}
          </div>
        )}
      </div>
    : null
    }
  </>
  )
}
 
export default Reccomendations;
