import React from 'react';


const clientId = '534b84117d9849018758b2f1688ea8fa';
const redirectURI = 'http://localhost:3000';
const scopes = [
  'streaming',
  'user-top-read',
  'playlist-modify-public'

];

const AUTH_URL = `https://accounts.spotify.com/authorize?client_id=${clientId}&response_type=code&redirect_uri=${redirectURI}&scope=${scopes.join('%20')}`

export default function Login() {
  return (
    <div className='btn login-btn'>
      <a href={AUTH_URL}>
        Log In to Spotify
      </a>
    </div>
  )
}
