import React, { useState } from 'react';
import Player from './Player';
import Seeding from './Seeding';
import Recommendations from './Recommendations';
import './dashboard.css';


export default function Dashboard({ token }) {
  // const [seeds, setSeeds] = useState([]);
  // const [seed_type, setSeedType] = useState('tracks');
  const [recommendations, setRecommendations] = useState([])
  const [displayRecs, setDisplayingRecs] = useState(false);
  const [currentTrack, setCurrentTrack] = useState(
    {
      name: '',
      album: { images: [{url:''}]},
      artists: [{name:''}]
    }
  );



  return (
    <div className="dashboard-container">
      <div className='upper-dash'>
        <Seeding
          token={token}
          // seeds={seeds}
          // seed_type={seed_type}
          // setSeeds={setSeeds}
          // setSeedType={setSeedType}
          // recommendations={recommendations}
          setRecommendations={setRecommendations}
          setDisplayingRecs={setDisplayingRecs}
        />
        <div style='flex-grow:1'>
        </div>
        <Recommendations
          token={token}
          recommendations={recommendations}
          setRecommendations={setRecommendations}
          displayRecs={displayRecs}
          // seeds={seeds}
          // seed_type={seed_type}
        />
      </div>
      <div className='lower-dash'>
        <Player
          token={token}
          track={currentTrack}
        />
      </div>
    </div>
  );
}
