import React from 'react';


const clientId = '534b84117d9849018758b2f1688ea8fa';
const clientSecret = '';
const redirectURI = 'http://localhost:3000';
const scopes = [
  'streaming',
  'user-top-read',
  'playlist-modify-public',
  'user-library-modify',
  'user-modify-playback-state',
];

const AUTH_URL = `https://accounts.spotify.com/authorize?client_id=${clientId}&redirect_uri=${redirectURI}&scope=${scopes.join('%20')}&response_type=token&show_dialog=true`


export const getAuthTokens = (hash) => {
  // Returns object of each parameter in the URL after Auth
  const stringAfterHash = hash.substring(1);
  const paramsInUrl = stringAfterHash.split('&');
  const tokens = paramsInUrl.map((param) => {
    return param.split('=');
  });
  const tokenObj = {};
  tokens.forEach((pair) => {
    tokenObj[pair[0]] = pair[1]
  });
  return tokenObj;
};


export default function Login() {
  const handleLogin = () => {
    window.location = AUTH_URL;
  };

  return (
    <div className='btn login-btn'>
      <h1>Welcome!</h1>
      <button onClick={ handleLogin }>
        Log in with Spotify
      </button>
    </div>
  )
}
