import { useState } from 'react';
const queryString = require('query-string');

// import axios from 'axios';
// import { getAuthTokens } from './components/Login';


const URL = 'https://api.spotify.com/v1';
// const { accessToken } = getAuthTokens(window.location.hash);

export const getAvailableDevices = async (token) => {
  const response = await fetch(URL + '/me/player/devices', {
    method: 'GET',
    headers: {
      "Authorization": "Bearer " + token,
      "Content-Type": "application/json",
    }
  });
  return response;
};

export const setActiveDevice = async (token, device_id) => {
// put /me/player
  const data = {device_ids:[device_id]}
  const response = await fetch(URL + 'me/player', {
    method: 'GET',
    headers: {
      "Authorization": "Bearer " + token,
    },
  })
};
