import { useState } from 'react';
import AddSeedButton from './AddSeedButton';
import SeedingSelect from './SeedingSelect';
import SeedItem from './SeedItem';
import { Container } from 'react-bootstrap';


const Seeding = () => {
  const seedingOptions = [
    {value: 'artist', text: 'Artist'},
    {value: 'genres', text: 'Genres'},
    {value: 'songs', text: 'Songs'},
    {value: 'personal', text: 'Your Top Songs'}
  ];

  const [selected, setSelected] = useState(seedingOptions[0].value);
  const [seeds, setSeeds] = useState([]);

  const handleChange = event => {
    setSelected(event.target.value);
  };
  const handleSearch = (searchInput) => {
    console.log('seeds before: ', seeds);
    // seeds.push({'text': searchInput, 'id': seeds.length});
    const new_seed = {'text': searchInput, 'id': seeds.length}
    setSeeds
    // if (!seeds.length) {
    //   setSeeds([...seeds, {'text': searchInput, 'id': seeds.length}]);
    // } else setSeeds([{'text': searchInput, 'id': 0}])
    // console.log('seeds after: ', seeds);

    console.log('seeds after: ', seeds);

  };

  return (
    <Container>
      <div className="seeding-container">
        <SeedingSelect
          options={seedingOptions}
          selected={selected}
          handleChange={handleChange}
        />
        { seeds.map((seed) => {
          console.log('map seed', seed);
            return <SeedItem id={seed.id} key={seed.id} seed={seed}/>
          })
        }
        {(seeds.length < 5 || selected !== 'personal') 
          ? <AddSeedButton 
              category={selected}
              handleSearch={handleSearch}
            /> 
          : null
        }
      </div>
    </Container>
  );
};

export default Seeding;
