import React from 'react';
const queryString = require('query-string');


const client_id = '534b84117d9849018758b2f1688ea8fa';
const client_secret = '8ce5dbf26b9547758c42311ae74f38ba';
const redirect_URI = 'http://localhost:3000';
const scopes = [
  'streaming',
  'user-top-read',
  'playlist-modify-public',
  'user-library-modify',
  'user-modify-playback-state',
];
const API_URL = 'https://accounts.spotify.com/api';
const AUTH_URL = `https://accounts.spotify.com/authorize?client_id=${client_id}&redirect_uri=${redirect_URI}&scope=${scopes.join('%20')}&response_type=code&show_dialog=true`;


export const fetchAccessTokens = async () => {
  const data = { grant_type: 'client_credentials' }
  const response =  await fetch(API_URL + '/token', {
    method: 'POST',
    body: queryString.stringify(data),
    headers: {
      'Authorization': 'Basic ' + window.btoa(client_id + ':' + client_secret),
      'Content-Type': 'application/x-www-form-urlencoded',
    },
  })
  .then(response => {
    response.json().then(response => {
      if(response.status > 200){
          console.log('Uh oh...', response.status);
          return false;
      }
      console.log('test: ', response);
      return response;
    })
  })
  .catch((err) => {
    console.log(err)
  });
}


export default function Login() {
  const handleLogin = () => {
    window.location = AUTH_URL;
  };

  return (
    <div className='btn login-btn'>
      <h1>Welcome!</h1>
      <button onClick={ handleLogin }>
        Log in with Spotify
      </button>
    </div>
  )
}
