import React, { useState } from 'react';
import { useEffect } from 'react';
import { getReccomendations } from '../api';


const Reccomendations = ({seeds, token}) => {
  const [display_recs, setDisplayingRecs] = useState(false);
  const [reccomendations, setReccomendations] = useState([]);

  useEffect(()=>{
    if(!seeds) return;
    getReccomendations(seeds, token)
    .then(response => response.json())
    .then(response => {
      console.log('response in get Reccomendations: ', response);
      setReccomendations(response.tracks);
    });
  }, [seeds])
  console.log('seeds: ', seeds);
  return(
  <>
    {seeds?.length
     ? <button onClick={setDisplayingRecs(true)}>
      Show Reccomendations
     </button>
    : null
    }
    {reccomendations.length && display_recs
    ? <div className="rec-container">
        <h3>Reccomendations: </h3>
        {reccomendations.map(reccomendation => 
          <div className='rec-item'>
            {reccomendation.name}
          </div>
        )}
      </div>
    : null
    }
  </>
  )
}
 
export default Reccomendations;
