import './App.css';
import Login from './components/Login'
import { getAuthTokens } from './components/Login';
import Dashboard from './components/Dashboard';
import { useEffect } from 'react';

// pull access token out of URL params
const code = new URLSearchParams(window.location.search).get('code');

function App() {
  useEffect(()=> {
    if(!window.location.hash) return;
    const {access_token, expires_in} = getAuthTokens(window.location.hash);
    console.log('access token: ', access_token);
    localStorage
  }, []);

  return (
    <div className="App">
      { code 
        ? <Dashboard code={code}/>
        : <Login/>
      }
    </div>
  );
}

export default App;
